  let imageTag = $(`<img src="${imageAssetUrlPath}" style="display: none; width: 30px;"/>`);
  $('body').append(imageTag);
  return imageTag;
}

function spanAsset(identifier) {
  let spanTag = $('<span style="font-size: 1.2em; display: none;">üõ†Ô∏è</span>');
