// import * as THREE from '../build/three.module.js';
import * as THREE from 'three';
import { OrbitControls } from './kmz/jsm/controls/OrbitControls.js';
import { KMZLoader } from './kmz/jsm/loaders/KMZLoader.js';

var camera, scene, renderer;

init();
function init() {
    let cube, cube2, cube3;

	scene = new THREE.Scene();

	var geometry = new THREE.BoxGeometry( 100, 100, 10 );
	var material = new THREE.MeshBasicMaterial( {color: 0x00ff00} );

	// §
	cube = new THREE.Mesh( geometry, material );
    scene.add( cube );

    var geometry2 = new THREE.BoxGeometry( 100, 100, 10 );
	var material2 = new THREE.MeshBasicMaterial( {color: 0x0000ff} );

	// §
	cube2 = new THREE.Mesh( geometry2, material2 );
    scene.add( cube2 );

    for(let i=0;i++;i<100) {
        moveCube(cube2);
        render();
    }

	scene.background = new THREE.Color( 0x999999 );
	var light = new THREE.DirectionalLight( 0xffffff );
	light.position.set( 0.5, 1.0, 0.5 ).normalize();
	scene.add( light );
	camera = new THREE.PerspectiveCamera( 35, window.innerWidth / window.innerHeight, 1, 500 );
	camera.position.y = 5;
	camera.position.z = 10;
	scene.add( camera );
	var grid = new THREE.GridHelper( 50, 50, 0xffffff, 0x555555 );
	scene.add( grid );
	renderer = new THREE.WebGLRenderer( { antialias: true } );
	renderer.setPixelRatio( window.devicePixelRatio );
	renderer.setSize( window.innerWidth, window.innerHeight );
	document.body.appendChild( renderer.domElement );
	var loader = new KMZLoader();

	// 2 or 1
	loader.load( '/kmz_models/1.kmz', function ( kmz ) {
		kmz.scene.position.y = 0.5;
		scene.add( kmz.scene );
		render();
	} );
	var controls = new OrbitControls( camera, renderer.domElement );
	controls.addEventListener( 'change', render );
	controls.update();
	window.addEventListener( 'resize', onWindowResize, false );
}
function onWindowResize() {
	camera.aspect = window.innerWidth / window.innerHeight;
	camera.updateProjectionMatrix();
	renderer.setSize( window.innerWidth, window.innerHeight );
	render();
}
function render() {
	renderer.render( scene, camera );
}


