// import Sortable from 'sortablejs';


window.showCopyAndPasteModal = function() {
    $("#myModal").css("display", "block");
    $("#myModal").css("opacity", 1);

    $("#myModal").draggable({
      handle: ".modal-header"
    });

    Sortable.create($(".sortable")[0]);
}
